    val oid = k._1
    val cid = k._2
    val pid = k._3

    val seatInfo = vals.map{cs => {
        SeatInfo(
            section = cs.section,
            row = cs.row,
            seat = cs.seat,
            sale_refund = if (cs.quantity == 1) "sale" else "refund",
            price_type = cs.priceTypeName,
            gross_price = cs.price + cs.faceValueFees,
            barcode = cs.barcode
        )
    }}

    val cs = vals.next

