def lowerNullCheck(s: String): Option[String] = {
    val str = Option(s).getOrElse(return None)
    Some(str.toLowerCase().replaceAll("\\s+", ""))
}

val lowerWithNullCheck = udf[Option[String], String](lowerNullCheck)

